import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Typography, Box, Card, Button, Stack, CssBaseline } from '@mui/material';
import { createTheme, ThemeProvider, styled } from '@mui/material/styles';

// Tema Gelap
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

// Styling untuk Card (User Info)
const StyledCard = styled(Card)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  width: '100%',
  maxWidth: '500px',
  margin: 'auto',
}));

// Styling untuk Container
const CheckPageContainer = styled(Stack)(({ theme }) => ({
  minHeight: '100vh',
  justifyContent: 'center',
  alignItems: 'center',
  padding: theme.spacing(2),
  background: 'radial-gradient(circle, rgba(25,25,60,1) 0%, rgba(0,0,0,1) 100%)',
}));

const CheckPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Ambil query parameter dari URL
  const queryParams = new URLSearchParams(location.search);
  const idTele = queryParams.get('idTele') || 'Unknown ID';
  const devisi = queryParams.get('devisi') || 'N/A';
  const subDevisi = queryParams.get('subDevisi') || 'N/A';

  // Data pengguna yang ditampilkan
  const user = {
    idTele: idTele,
    email: 'user@example.com', // Placeholder email, bisa disesuaikan jika ada data dinamis
    nama: 'John Doe',
    telepon: '08123456789',
    devisi: devisi,
    subDevisi: subDevisi,
  };

  return (
    <ThemeProvider theme={darkTheme}>
    <CssBaseline />
    <CheckPageContainer>
      <StyledCard sx={{ textAlign: 'center' }}> {/* Pusatkan semua teks */}
        {/* Judul Halaman */}
        <Typography
          component="h1"
          variant="h5"
          sx={{ color: 'white', marginBottom: 2 }}
        >
          User Information
        </Typography>

        {/* Informasi Pengguna */}
        <Box id="userInfo">
          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            ID Telegram: <span id="idTele">{user.idTele}</span>
          </Typography>

          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            Email: <span id="email">{user.email}</span>
          </Typography>

          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            Nama: <span id="nama">{user.nama}</span>
          </Typography>

          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            Telepon: <span id="telepon">{user.telepon}</span>
          </Typography>

          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            Devisi: <span id="devisi">{user.devisi}</span>
          </Typography>

          <Typography sx={{ color: 'white', marginBottom: 1 }}>
            Sub-Devisi: <span id="subDevisi">{user.subDevisi}</span>
          </Typography>
        </Box>

        {/* Tombol Confirm */}
        <Button
          variant="contained"
          sx={{
            backgroundColor: 'white',
            color: 'black',
            '&:hover': { backgroundColor: '#f0f0f0' },
            mt: 2,
          }}
        >
          CONFIRM
        </Button>
      </StyledCard>
    </CheckPageContainer>
  </ThemeProvider>
  );
};

export default CheckPage;
